import { Component } from 'react';
import { Movies } from '../../Components/Movies/Movies';
import { Search } from '../../Components/Search/Search'
import './Main.css';

class Main extends Component {
    state = {
        movies: [],
    }

    componentDidMount() {
        fetch(`http://www.omdbapi.com/?apikey=8d1cc66c&s=Avengers`)
            .then(response => response.json())
            .then(post => {
                if(post.Search === undefined) {
                    this.setState({
                        movies: [],
                    })
                } else {
                    this.setState({
                        movies: post.Search
                    })
                }
            })
    }

    changeBg = (id) => {
        const fillArr = this.state.movies.filter(item => item.imdbID === id);
        document.body.style.backgroundImage = `url(${fillArr[0].Poster})`;
    }

    searchMovie = (movieName, movieType) => {
        fetch(`http://www.omdbapi.com/?apikey=8d1cc66c&s=${movieName}${movieType !== 'all' ? `&type=${movieType}` : ''}`)
        .then(response => response.json())
        .then(post => {
            if(post.Search === undefined) {
                this.setState({
                    movies: [],
                })
            } else {
                this.setState({
                    movies: post.Search
                })
            }
        })
    }

    render() {
        return (
            <main className='mainContainer'>
                <Search searchMovie={this.searchMovie} />
                { this.state.movies.length ?
                <Movies
                    changeBg={this.changeBg}
                    searchMovie={this.searchMovie}
                    movies={this.state.movies}
                />
                : <h4 style={{
                    textAlign: 'center',
                    color: '#c2c2c2',
                }}>Not Found</h4> }
            </main>
        )
    }
}

export { Main }
