import React, { Component } from "react";
import { Movie } from "../Movie/Movie";
import { Preloader } from "../Preloader/Preloader";
import './Movies.css';

class Movies extends Component {
    state = {
        movies: []
    }

    changeBg = (id) => {
        const fillArr = this.state.movies.filter(item => item.imdbID === id);
        document.body.style.backgroundImage = `url(${fillArr[0].Poster})`;
    }

    componentDidMount() {
        fetch('http://www.omdbapi.com/?apikey=8d1cc66c&s=Shrek')
            .then(response => response.json())
            .then(post => {
                if(post.Search === undefined) {
                    this.setState({
                        movies: [],
                    })
                } else {
                    this.setState({
                        movies: post.Search
                    })
                }
            })
    }

    render() {
        const {movies} = this.state;
        return (
            <div className="movie-container">
                {  movies.
                    movies.map(movie => {
                        return ( 
                            <Movie
                                key={movie.imdbID}
                                movieId={movie.imdbID}
                                title={movie.Title}
                                year={movie.Year}
                                poster={movie.Poster}
                                changeBg = {this.changeBg}
                            />
                        )
                    })
                }
            </div>
        )
    }
}

export { Movies }
